import { Transition } from '@nuxt/types/app'
import { NuxtOptionsBuild } from '@nuxt/types/config/build'
import { NuxtOptionsCli } from '@nuxt/types/config/cli'
import { NuxtOptionsEnv } from '@nuxt/types/config/env'
import { NuxtOptionsFeatures } from '@nuxt/types/config/features'
import { NuxtOptionsFetch } from '@nuxt/types/config/fetch'
import { NuxtOptionsGenerate } from '@nuxt/types/config/generate'
import { NuxtOptionsHead } from '@nuxt/types/config/head'
import { NuxtOptionsHooks } from './hooks'
import { NuxtOptionsGlobals } from '@nuxt/types/config/globals'
import { NuxtOptionsLoading, NuxtOptionsLoadingIndicator } from '@nuxt/types/config/loading'
import { NuxtOptionsModule } from '@nuxt/types/config/module'
import { NuxtOptionsPlugin } from '@nuxt/types/config/plugin'
import { NuxtOptionsRender } from '@nuxt/types/config/render'
import { NuxtOptionsRouter } from '@nuxt/types/config/router'
import { NuxtOptionsRuntimeConfig } from '@nuxt/types/config/runtime'
import { NuxtOptionsServer } from '@nuxt/types/config/server'
import { NuxtOptionsServerMiddleware } from '@nuxt/types/config/server-middleware'
import { NuxtOptionsVueConfiguration } from '@nuxt/types/config/vue-configuration'
import { NuxtOptionsWatchers } from '@nuxt/types/config/watchers'

export { Module } from '@nuxt/types/config/module'
export { ServerMiddleware } from '@nuxt/types/config/server-middleware'

/**
 * @deprecated Use NuxtConfig instead
*/
export interface Configuration extends Record<string, any> {}

export interface NuxtOptions extends Configuration {
  build: NuxtOptionsBuild
  buildDir: string
  buildModules: NuxtOptionsModule[]
  cli: NuxtOptionsCli
  css: string[]
  dev: boolean
  dir: { [key in 'app' | 'assets' | 'layouts' | 'middleware' | 'pages' | 'static' | 'store']?: string }
  env: NuxtOptionsEnv
  extensions: string[]
  features: NuxtOptionsFeatures
  fetch: NuxtOptionsFetch
  generate: NuxtOptionsGenerate
  export: NuxtOptionsGenerate
  globalName: string
  globals: NuxtOptionsGlobals
  head: NuxtOptionsHead
  hooks: NuxtOptionsHooks
  ignorePrefix: string
  ignore: string[]
  layoutTransition: Transition
  loading: NuxtOptionsLoading | false | string
  loadingIndicator: NuxtOptionsLoadingIndicator | false | string
  mode: 'spa' | 'universal'
  target: 'server' | 'static'
  modern: 'client' | 'server' | boolean
  modules: NuxtOptionsModule[]
  modulesDir: string[]
  plugins: NuxtOptionsPlugin[]
  privateRuntimeConfig: NuxtOptionsRuntimeConfig
  publicRuntimeConfig: NuxtOptionsRuntimeConfig
  render: NuxtOptionsRender
  rootDir: string
  router: NuxtOptionsRouter
  server: NuxtOptionsServer
  serverMiddleware: NuxtOptionsServerMiddleware[]
  srcDir: string
  transition: Transition
  'vue.config': NuxtOptionsVueConfiguration
  watch: string[]
  watchers: NuxtOptionsWatchers
}

export type NuxtConfig = Partial<NuxtOptions>
